  generate_dictionary_script <- function(data) {
    #, output_file = "generated_script.R") {
    # Ensure data is a tibble
    if (!is.data.frame(data)) {
      stop("Input data must be a tibble or data.frame.")
    }

    # Get column names
    col_names <- names(data)

    # Initialize the script
    script <- "library(tibble)\nlibrary(dplyr)\n\ndata_tibble <- tibble::tibble("

    # if there is a empty space in any of col_names, wrap that individual col_name in backticks
    col_names <- ifelse(grepl(" ", col_names), paste0("`", col_names, "`"), col_names)

    script <- paste0(script, paste(col_names, "= character()", collapse = ",\n  "), ") %>% \n")

    # Add rows using add_row()
    for (i in seq_len(nrow(data))) {
      row_values <- purrr::map_chr(data[i, ], ~ifelse(is.na(.x), "NA", paste0('"', .x, '"')))
      add_row_command <- paste0("  dplyr::add_row(", paste(col_names, "=", row_values, collapse = ", "), ")")
      script <- paste0(script, "  ", add_row_command, " %>% \n")
    }

    # Remove trailing pipe (%>%) from last row
    script <- gsub(" %>% \\n$", "\n", script)

    cat(script)

    # # Write script to file
    # writeLines(script, output_file)
    #
    # message("Script written to ", output_file)
